namespace Exercicio026 // fatorial :){    internal class Program    {        static void Main(string[] args)        {   int numeroD;            Console.WriteLine("Digite o valor para definirmos o fatorial: ");            numeroD = Convert.ToInt32(Console.ReadLine());                        for (int i = numeroD - 1; i > 0; i--)            {                numeroD = numeroD * i;            }            Console.WriteLine("O fatorial é : " + numeroD);        }    }}